INSERT INTO `policy_data` VALUES (1, '《2025年高校毕业生\"三支一扶\"计划》', '三支一扶、2025招募、中央财政支持', '明确中央财政支持招募34430名高校毕业生到基层从事支教、支农、支医和帮扶乡村振兴服务，要求各地优化招募选拔流程，扩大招募规模并向脱贫地区、西部地区、东北地区、革命老区、民族地区和边疆地区倾斜，对口支援省市协助西藏、新疆招募，强化能力提升与管理使用，规范管理使用基层服务单位，落实待遇保障，加强日常管理与联系服务，拓展留用渠道落实机关事业单位考录支持政策，鼓励服务期满人员留当地工作并纳入人才工作部署跟踪培养，加强组织领导并严格财经纪律', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (2, '《2025年高校毕业生\"三支一扶\"计划》', '支教岗位、师范专业、基层教育', '支教岗位面向汉语言文学、数学与应用数学、英语、物理学、化学、生物学、历史学、地理科学、思想政治教育、音乐学、美术学、体育教育等师范类专业毕业生，中央财政支持8000人次能力提升专项培训，基层服务单位需加强培养使用，落实待遇保障，强化日常管理与联系服务，服务期满可落实机关事业单位考录支持政策，鼓励留当地工作，纳入人才跟踪培养体系', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (3, '《2025年高校毕业生\"三支一扶\"计划》', '支农岗位、农业专业、乡村振兴', '支农岗位面向农学、园艺、植物保护、农业资源与环境、动物科学、动物医学、林学、园林、农业机械化及其自动化等农业类专业毕业生，中央财政支持8000人次专项培训，培训体系围绕基层需求确定重点，完善分层分类培训，服务期满可落实机关事业单位考录支持政策，鼓励留当地工作，拓展留用渠道，支持创新创业', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (4, '《2025年高校毕业生\"三支一扶\"计划》', '支医岗位、医学专业、基层医疗', '支医岗位面向临床医学、中医学、针灸推拿学、护理学、药学、医学检验技术、医学影像学等医学类专业毕业生，基层服务单位落实待遇保障，加强日常管理，服务期满纳入人才工作部署跟踪培养，鼓励服务期满人员留当地工作，拓宽职业成长空间，落实编制政策，强化日常联系服务', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (5, '《2025年高校毕业生\"三支一扶\"计划》', '乡村振兴、经济管理类、乡村治理', '帮扶乡村振兴岗位面向经济学类（农村区域发展、农林经济管理）、公共管理类（行政管理、土地资源管理）、社会学类（社会工作）等专业毕业生参与乡村产业发展与治理，拓展留用渠道支持创新创业，纳入人才工作部署跟踪培养，加强政策宣传与典型经验总结，提升宣传效果', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (6, '《2025年高校毕业生\"三支一扶\"计划》', '服务期满、编制政策、留用渠道', '明确服务期满人员可落实机关事业单位考录、招聘支持政策，拓宽基层留人用人渠道，鼓励服务期满人员留当地工作，符合条件的纳入人才工作部署跟踪培养，加强政策宣传与典型经验总结，提升宣传效果，强化多方协同，加快实施进度', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (7, '《2025年高校毕业生\"三支一扶\"计划》', '能力提升、专项培训、分层分类', '中央财政支持8000人次能力提升专项培训，围绕基层需求确定培训重点，完善分层分类培训体系，基层服务单位加强培养使用，强化日常管理与联系服务，提高培训精准性，多方筹集资金，健全市场化投入机制，鼓励社会资本投入', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (8, '《\"十四五\"职业技能培训规划》', '职业培训、7500万人次、补贴性培训', '提出到2025年组织实施政府补贴性培训7500万人次以上，其中农民工职业技能培训3000万人次以上，新增取得职业资格或技能等级证书人员4000万人次以上，新增公共实训基地200个，完善终身职业技能培训制度，健全共建共享培训体系，提升培训有效性，加强标准化建设，加强统筹规划与组织领导', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (9, '《\"十四五\"职业技能培训规划》', '高技能人才、800万人次、公共实训基地', '目标新增高技能人才800万人次以上，新增公共实训基地200个，完善技能人才职业发展通道，探索\"新八级\"职业技能等级制度，实现技能人才横向贯通发展，推出完善评价体系等措施，提高待遇水平，营造良好社会导向，让职业技能培训扩容提质', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (10, '《\"十四五\"职业技能培训规划》', '新八级工、职业发展、横向贯通', '探索\"新八级\"职业技能等级制度，实现技能人才纵向职业技能等级阶梯与专业技术人才横向贯通发展，完善技能人才评价体系，为技术工人突破职业发展天花板创造条件，提高技能劳动者待遇水平，拓展职业发展通道，营造良好社会导向', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (11, '《\"十四五\"职业技能培训规划》', '产训结合、企业主体、精准培训', '首次提出产训结合理念与举措，强调发挥企业培训主体作用，提高院校培训针对性，促进培训机构健康发展，发挥公共实训基地基础支撑作用，围绕产业和企业需求开展精准培训，营造良好社会导向，提高劳动者待遇，提升培训供给能力', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (12, '《\"十四五\"职业技能培训规划》', '数字技能、新职业培训、职业分类大典', '从修订职业分类大典标注数字技能类职业、开发培训标准教材、推进数字技能职业技能等级认定、推动数字技能相关比赛、培育数字技能培训基地等五个方面助力新经济职业培训，适应数字经济发展，加强数字技能人才培养，完善数字技能评价体系，推动数字技能普及', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (13, '《\"十四五\"职业技能培训规划》', '制造业技能、机械专业培训、高级技工', '面向机械设计制造及其自动化、材料成型及控制工程、工业工程等专业学生开展职业技能培训，向高级技工、技师方向发展，满足制造业对高层次技能人才需求，提升实操技能，产训结合提高精准性，发挥公共实训基地作用，鼓励企业主体参与培训，提升培训供给能力', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (14, '《\"十四五\"职业技能培训规划》', '信息技术、大数据培训、物联网', '面向计算机科学与技术、软件工程、大数据技术等专业学生开展数字技能培训，依据新修订的职业分类大典及开发的培训标准，提升在大数据、物联网等领域的数字技能，适应新经济需求，推进等级认定和培训基地建设，培育数字技能人才队伍，加强数字技能职业标准开发，推动数字技能相关比赛', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (15, '《\"十四五\"职业技能培训规划》', '职教教师、师资培养、培训供给能力', '支持职业技术教育等相关专业大学生学习先进培训理念与方法，参与高质量职业技能培训教学工作，提升培训供给能力，减少低端培训，增加中高端培训，培育专业师资队伍，鼓励社会资本投入，健全市场化机制，加强培训标准化建设，提高培训质量，动态调整培训专业设置补贴项目', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (16, '《\"十四五\"职业技能培训规划》', '应用型专业、产训结合、就业创业', '面向物流管理、电子商务、酒店管理等应用型专业学生，结合产训结合理念围绕产业发展需求进行职业技能培训，提高就业创业能力，实现技能成才，促进培训机构健康发展，营造良好社会导向，提高劳动者待遇，加强标准化建设，提升培训有效性，完善共建共享培训体系', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (17, '《\"十四五\"职业技能培训规划》', '农民工、培训目标、终身培训', '明确农民工职业技能培训3000万人次以上，健全终身职业技能培训制度，提升培训服务有效性，加强标准化建设，完善共建共享培训体系，发挥公共实训基地基础支撑作用，多方筹集资金，统筹就业补助资金，严格财经纪律，加强组织领导，开展总结评估，注重高层次技能人才培训', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (18, '《\"十四五\"职业技能培训规划》', '技能评价、证书体系、等级认定', '新增取得职业资格或技能等级证书人员4000万人次以上，完善技能人才评价体系，推进数字技能职业技能等级认定，加强技能人才队伍建设，推动相关技能竞赛，提高培训质量，加强标准化建设，完善技能人才职业发展通道，探索技能人才与专业技术人才横向贯通发展，推出完善评价体系措施', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (19, '《\"十四五\"职业技能培训规划》', '培训资金、就业补助、社会资本', '统筹利用就业补助资金，健全市场化投入机制，鼓励社会资本投入，加强资金监管和绩效评估，多方筹集资金，加强统筹规划与组织领导，开展总结评估并加强工作交流，严格财经纪律，注重高层次技能人才培训，动态调整培训专业设置补贴项目，加强标准化建设', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
INSERT INTO `policy_data` VALUES (20, '《\"十四五\"职业技能培训规划》', '待遇提高、激励政策、社会导向', '强调提高技能劳动者待遇水平，营造良好社会氛围，让职业技能培训真正扩容提质，拓展职业发展通道，为技术工人向特级技师等更高层级发展创造条件，加强标准化建设，提升培训供给能力，减少低端培训，增加中高端培训，实现技能成才目标，加强政策宣传，提高培训质量', '2025-11-16 21:07:20', '2025-11-16 21:07:20');
